function scrollBar(scrollTop) {

    var porcentaje = 0;
    porcentaje = ((100 * scrollTop) / document.querySelector('body').offsetHeight) * 1.025;
    document.querySelector('#porcentaje span').style.width = porcentaje + '%';
}

function controlGallery(scrollTop) {

    directionScroll();

    for (var i = 0; i < gallery.length; i++) {

        init_gallery[i] = contentsGallery[i].offsetTop + gallery[i].offsetTop;

        if (scrollTop >= init_gallery[i]) {

            end_gallery[i] = contentsGallery[i].offsetHeight + gallery[i].offsetTop - window.innerHeight;

            for (var x = 0; x < imgs[i].length; x++) {
                if (direction == 'up') {
                    var initUp = imgs[i][x].offsetTop + gallery[i].offsetTop;
                    if (scrollTop > initUp - 100 && scrollTop > initUp + 50) {
                        if (x % 2 == 0 && imgs[i][x + 1] && (x + 1) != fondoActual[i]) {
                            var content_img = imgs[i][x + 1].innerHTML;
                            fondos[i].innerHTML = content_img;
                            fondoActual[i] = x + 1;
                        }
                    }
                }

                if (direction == 'down') {
                    var initDown = imgs[i][x].offsetTop + gallery[i].offsetTop;
                    if (scrollTop > initDown - 150 && scrollTop < initDown + 150) {
                        if (x % 2 == 0 && imgs[i][x - 1] && (x - 1) != fondoActual[i]) {
                            var content_img = imgs[i][x - 1].innerHTML;
                            fondos[i].innerHTML = content_img;
                            fondoActual[i] = x - 1;
                        }
                    }
                }
            }

            if (scrollTop > end_gallery[i]) {
                fondos[i].style.position = "absolute";
                fondos[i].style.bottom = 0;
                fondos[i].style.top = 'auto';
            } else {
                fondos[i].style.position = "fixed";
            }

        } else {
            fondos[i].style.position = "";
        }
    }
}

function initGallery() {
    for (var i = 0; i < gallery.length; i++) {

        var galleryItem = gallery[i].id,
            fondo = document.querySelector("#" + galleryItem + " .fondo"),
            imgs_items = document.querySelectorAll("#" + galleryItem + " .gallery_item"),
            contentGallery = document.querySelector("#" + galleryItem + " .content_gallery_items");

        fondos.push(fondo);
        imgs.push(imgs_items);
        contentsGallery.push(contentGallery);
        init_gallery.push(contentsGallery[i].offsetTop + gallery[i].offsetTop);
        end_gallery.push(contentsGallery[i].offsetHeight + gallery[i].offsetTop - window.innerHeight);

        for (var z = 0; z < imgs[i].length; z++) {
            if (z % 2 == 0) {
                if (imgs[i][z + 1]) {
                    imgs[i][z + 1].style.visibility = "hidden";
                }
            }
        }

        var content_img = imgs[i][1].innerHTML;
        fondos[i].innerHTML = content_img;
        fondoActual.push('2');
    }
}

function directionScroll() {
    var st = window.pageYOffset || document.documentElement.scrollTop;

    if (st > lastScrollTop) {
        direction = 'up';
    } else {
        direction = 'down';
    }
    lastScrollTop = st <= 0 ? 0 : st;
}

window.onload = function() {
